Code
#prep data
report &lt;- coffeechain %&gt;%
  mutate(Year = year(Date)) %&gt;%
  filter(Year == "2013") %&gt;%
  mutate (Month = month(Date, 
                        label = TRUE, 
                        abbr = TRUE)) %&gt;%
  group_by(Product, Month) %&gt;%
  summarise(Sales = sum(Sales)) %&gt;%
  ungroup()

#convert the report data.frame into list columns.
report %&gt;%
  group_by(Product) %&gt;%
  summarize('Monthly Sales' = list(Sales), 
            .groups = "drop")


